-- extract input to lines in table
create or replace table input as select regexp_split_to_table(trim(getvariable('example'), E'\n '), '\n\s*') as line;

with recursive floors as (
    select
        1 as idx,
        substring(line,1,1) as char -- first char
    from input
    union all
    select 
        idx + 1,
        substring(line, idx+1,1) as char -- next char
    from floors, input
    where idx < len(line) -- terminate at end of string
),
movement as (
    select
        idx,
        case
            when char = '(' then 1
            when char = ')' then -1
            else 0
        end as move
    from floors
),
cumulative as (
    select
        idx,
        move,
        sum(move) over(order by idx) as current_floor
    from movement
),
part1 as (
    select
        'part1' as part,
        current_floor as answer 
    from cumulative 
    order by idx desc 
    limit 1
),
part2 as (
    select
    'part2' as part,
    idx as answer
    from cumulative
    where current_floor = -1
    order by idx asc
    limit 1
)
select * from part1
union all 
select * from part2;